mapper.select:
select * from auction_item

    and title like concat('%',#{title},'%')
    and status = #{status}
    and current_max_price >= #{minPrice}
    and current_max_price  <=  #{maxPrice}
    and listing_status = 1

mapper.update:
update auction_item
title = #{title}, image = #{image}, initial_price = #{initialPrice}, description = #{description}, start_time = #{startTime}, end_time = #{endTime}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select * from auction_item

    and title like concat('%',#{title},'%')
    and status = #{status}
    and current_max_price >= #{minPrice}
    and current_max_price  <=  #{maxPrice}

mapper.update:
update auction_item
set current_max_price = #{currentMaxPrice}, current_max_user_id = #{currentMaxUserId}
where id = #{itemId} and (current_max_price is null or current_max_price  <  #{currentMaxPrice})

update auction_item
set listing_status = #{listingStatus}
where id = #{id}
