mapper.update:
update shopping_cart
set number = number + 1

    user_id = #{userId}

        AND dish_id = #{dishId}


        AND setmeal_id = #{setmealId}


        AND dish_flavor = #{dishFlavor}

update shopping_cart
set number = number - 1

    user_id = #{userId}

        AND dish_id = #{dishId}


        AND setmeal_id = #{setmealId}


        AND dish_flavor = #{dishFlavor}

mapper.delete:
delete from shopping_cart

    user_id = #{userId}

        AND dish_id = #{dishId}


        AND dish_flavor = #{dishFlavor}


        AND setmeal_id = #{setmealId}

mapper.select:
select number from shopping_cart

    user_id = #{userId}

        AND dish_id = #{dishId}


        AND setmeal_id = #{setmealId}


        AND dish_flavor = #{dishFlavor}


limit 1
